const MonRadio = () =>{



  const [Non, setNon] = useState(true); 

  const Text = () =>{
    setNon(!Non)

  }

  const [Sexe, setSexe] = useState("Monsieur"); 

  const Monsieur = () =>{
    setSexe(Sexe=>"Monsieur"); 
    console.log(Sexe);
  }

  const Madame = () =>{
    setSexe(Sexe => "Madame"); 
    console.log(Sexe);

  }
  const contexValue1 = {

    choix: Sexe
  }



    if (Non){
    return(
    <div className="MainApp">


        <div className="container">
   <div className="row">
    <div className="col-sm-12">
