import React, {useState, use} from 'react';
import './App.css';
import Page1 from './Pages/Page1';
import Page2 from './Pages/Page2';
import Page3 from './Pages/Page3';
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import ContextNom from './Pages/ContextNom';


function App() {

  const contexValue = {
    theme: "blancheee",
  }



  const [Non, setNon] = useState('Non'); 
  const [name, setName] = useState('nom'); 

  function handleChangeName(e) { 
    setName(e.target.value); 
    setNon(Non === 'non')
  }

  useEffect(() => { 
    document.title = `MonChamp ${name}`; 
  }); 


  const [Prenom, setPrenom] = useState('Prenom'); 

  function handleChangePrenom(e) { 
    setPrenom(e.target.value); 
  }

  useEffect(() => { 
    document.title = `MonChamp ${Prenom}`; 

  }); 





  return (
    <div >
        <ContextNom.Provider value={contexValue}>
        <div>



        <div className='ChampText'>
            Peux-tu m'indiquer ton nom et prenom    
        </div>
        <div className='DivNom'>
        <input type="text" value={name} onChange={handleChangeName}/>
        </div>
        <div className='DivNom'>
        <input type="text" value={Prenom} onChange={handleChangePrenom}/>
        </div>

        <li>
              <Link to="/Page3">Pagee3nouveauS"</Link>
        </li>


    </div>




        <Router>
          <div>     
          {/* A <Switch> looks through its children <Route>s and
              renders the first one that matches the current URL. */}
          <Switch>
          <Route exact path="/">
              <Page1 />
            </Route>
            <Route exact path="/Page2">
              <Page2 />
            </Route>


            <Route exact path="/Page3">
              <Page3 />
            </Route>


          </Switch>
          </div> 
      </Router>
      </ContextNom.Provider>
    </div>
  );
}

export default App;
