import React, { useState } from 'react';
import { Button } from 'react-bootstrap';
/*import { Link } from "react-router-dom";*/
import './Page.css';

import MainNav3 from './MainNav3';
import ContextChoix from './ContextChoix';





const MonRadio = () =>{



  const [Non, setNon] = useState(true); 

  const Text = () =>{
    setNon(!Non)

  }

  const [Sexe, setSexe] = useState("Monsieur"); 

  const Monsieur = () =>{
    setSexe(Sexe=>"Monsieur"); 
    console.log(Sexe);
  }

  const Madame = () =>{
    setSexe(Sexe => "Madame"); 
    console.log(Sexe);

  }
  const contexValue1 = {

    choix: Sexe
  }



    if (Non){
    return(
    <div className="MainApp">


        <div className="container">
  <div className="row">
    <div className="col-sm-12">
        <div>Pouvez-vous indiquer votre sexe ?</div>

        <div>
        <div className="monsieur" onClick={Monsieur}>Monsieur</div> <br/><br/>
        <Button onClick={Madame}>Madame</Button><br/><br/>
        </div>
    </div>


  </div>

</div>

<Button className="Button" onClick={Text}>SUIVANT</Button>
    </div>
    );
    }
    else{
      return(
        <div>
          <ContextChoix.Provider value={contexValue1}>
           <MainNav3 />
           </ContextChoix.Provider> 
        </div>


      );
    }
}




export default MonRadio;
