import React, { useState } from 'react';
import { Button } from 'react-bootstrap';
/*import { Link } from "react-router-dom";*/
import './Page.css';
import ContextChoix from './ContextChoix';
import MainNav3 from './MainNav3';





const MonRadio = () =>{


  const [Non, setNon] = useState(true); 

  const Text = () =>{
    setNon(!Non)

  }



  const Monsieur = () =>{
    var sexe = "Monsieur";
    console.log(sexe);

  }
  const Madame = () =>{
    var sexe = "Madame";
    choix = sexe;
  }

  const contexValue = {
    choix: sexe
   }




    if (Non){
    return(
    <div className="MainApp">


        <div className="container">
  <div className="row">
    <div className="col-sm-12">
        <div>Pouvez-vous indiquer votre sexe ?</div>

      <form>
        <div className="radio">
          <label>
            <input type="radio" id="choix1" value="Monsieur" name="sexe" checked={true}/>
            Monsieur
          </label>
        </div>
        <div className="radio">
          <label>
            <input type="radio" id="choix2"  value="Madame" name="sexe" />
            Madame
          </label>
        </div> 
      </form>
        <div>
        <Button onClick={Monsieur}>Monsieur</Button> <br/><br/>
        <Button onClick={Madame}>Madame</Button><br/><br/>
        </div>
    </div>


  </div>

</div>

<Button onClick={Text}>MainNav3</Button>
    </div>
    );
    }
    else{
      return(
        <div>
           <ContextChoix.Provider value={contexValue}>
           <MainNav3 />
           </ContextChoix.Provider>
        </div>


      );
    }
}




export default MonRadio;
