import React, { useState } from 'react';
import { Button } from 'react-bootstrap';
/*import { Link } from "react-router-dom";*/
import './Page.css';

import MainNav3 from './MainNav3';
import ContextChoix from './ContextChoix';





const MonRadio = () =>{

  const [Sexe, setSexe] = useState(Monsieur); 

  const [Non, setNon] = useState(true); 

  const Text = () =>{
    setNon(!Non)

  }


  const Monsieur = () =>{
    varsexe = "Monsieur"


  }

  const Madame = () =>{
    sexe = "Madame"
    console.log(sexe)

  }
  const contexValue1 = {

    choix: sexe
  }



    if (Non){
    return(
    <div className="MainApp">


        <div className="container">
  <div className="row">
    <div className="col-sm-12">
        <div>Pouvez-vous indiquer votre sexe ?</div>

      <form>
        <div className="radio">
          <label>
            <input type="radio" id="choix1" value="Monsieur" name="sexe" checked={true}/>
            Monsieur
          </label>
        </div>
        <div className="radio">
          <label>
            <input type="radio" id="choix2"  value="Madame" name="sexe" />
            Madame
          </label>
        </div> 
      </form>
        <div>
        <Button onClick={Monsieur}>Monsieur</Button> <br/><br/>
        <Button onClick={Madame}>Madame</Button><br/><br/>
        </div>
    </div>


  </div>

</div>

<Button onClick={Text}>MainNav3</Button>
    </div>
    );
    }
    else{
      return(
        <div>
          <ContextChoix.Provider value={contexValue1}>
           <MainNav3 />
           </ContextChoix.Provider> 
        </div>


      );
    }
}




export default MonRadio;
